analyzeparams.value:
$updaterFilename = "C:\Program Files (x86)\Google\Update\GoogleUpdate.exe";
if (Test-Path -Path $updaterFilename) {
    $Global:CTXOE_Result = $False;
    $Global:CTXOE_Details = "Updater present";
} else {
    $Global:CTXOE_Result = $True;
    $Global:CTXOE_Details = "Updater not present";
}

executeparams.value:
$updaterFilename = "C:\Program Files (x86)\Google\Update\GoogleUpdate.exe";
$updateNewName = "GoogleUpdate.original";

try {
    Rename-Item -Path $updaterFilename -NewName $updateNewName -Force;
    $Global:CTXOE_Result = $True;
    $Global:CTXOE_Details = "Update renamed";
} catch {
    $Global:CTXOE_Result = $False;
    $Global:CTXOE_Details = "Error while renaming";
}

rollbackparams.value:
$updaterFilename = "C:\Program Files (x86)\Google\Update\GoogleUpdate.original";
$updateNewName = "GoogleUpdate.exe";

try {
    Rename-Item -Path $updaterFilename -NewName $updateNewName -Force;
    $Global:CTXOE_Result = $True;
    $Global:CTXOE_Details = "Update renamed to original";
} catch {
    $Global:CTXOE_Result = $False;
    $Global:CTXOE_Details = "Error while renaming";
}

