$out = fsutil resource setautoreset false C:\ 2>&1 | Out-String
if ($LASTEXITCODE -eq 0) {
    $Global:CTXOE_Result = $true
} else {
    $Global:CTXOE_Result = $false
}
$Global:CTXOE_Details = $out.Trim()

