analyzeparams.value:
# Get power plan through registry (version 1803 and up)
                 If ((Get-ItemPropertyValue -LiteralPath HKLM:\SYSTEM\CurrentControlSet\Control\Power\User\PowerSchemes -Name ActivePowerScheme) -eq '8c5e7fda-e8bf-4a96-9a85-a6e23a8c635c') {
                    $Global:CTXOE_Result = $True;
		    $Global:CTXOE_Details = "High Performance mode selected";
                    Return;
                 } Else {
                    $Global:CTXOE_Result = $False;
		    $Global:CTXOE_Details = "High Performance mode not selected";
                    Return;
                  }

executeparams.value:
# Set power plan to "High Performance"
$ModeID = "8c5e7fda-e8bf-4a96-9a85-a6e23a8c635c"; #This is High Performance

$DesiredMode = Get-ItemPropertyValue -LiteralPath HKLM:\SYSTEM\CurrentControlSet\Control\Power\User\PowerSchemes -Name ActivePowerScheme

# Check if mode is currently selected. If yes, skip. 
If ($DesiredMode -eq $ModeID) {
  $Global:CTXOE_Result = $True; 
  $Global:CTXOE_Details = "Mode already configured, skipping";    
  Return;
}

# Different mode is currently selected. Change the active mode. 
PowerCfg /SetActive $ModeID;
$Global:CTXOE_Result = $True;
$Global:CTXOE_Details = "Power plan changed to High Performance mode";

rollbackparams.value:
# Rollback power plan to "Balanced"
$ModeID = "381b4222-f694-41f0-9685-ff5bb260df2e"; #This is Balanced

$DesiredMode = Get-ItemPropertyValue -LiteralPath HKLM:\SYSTEM\CurrentControlSet\Control\Power\User\PowerSchemes -Name ActivePowerScheme

# Check if mode is currently selected. If yes, skip. 
If ($DesiredMode -eq $ModeID) {
  $Global:CTXOE_Result = $True; 
  $Global:CTXOE_Details = "Mode already configured, skipping";    
  Return;
}

# Different mode is currently selected. Change the active mode. 
PowerCfg /SetActive $ModeID;
$Global:CTXOE_Result = $True; 
$Global:CTXOE_Details = "Power plan changed to Balanced mode";

params.value:
Write-Host "This optimization does not support analyze or rollback modes, skipping...";
$Global:CTXOE_Result = $True;
$Global:CTXOE_Details = "Analyze/Rollback not supported/required, skipping...";
Return;

executeparams.value:
If ($(Test-Path $Env:WinDir\Microsoft.Net\Framework64\v4.0.30319\ngen.exe) -eq $True) {
. $Env:WinDir\Microsoft.Net\Framework64\v4.0.30319\ngen.exe Update;
 $Global:CTXOE_Details = ".NET assemblies optimized";
} Else {
 $Global:CTXOE_Details = "Skipping, not 64 bit NGEN not found";
}
$Global:CTXOE_Result = $True;

params.value:
Write-Host "This optimization does not support analyze or rollback modes, skipping...";
$Global:CTXOE_Result = $True;
$Global:CTXOE_Details = "Analyze/Rollback not supported/required, skipping...";
Return;
